import{_ as e,c as t,o as a,d as r}from"./app.0523c422.js";const u=JSON.parse('{"title":"Как работает этот сайт","description":"","frontmatter":{},"headers":[{"level":2,"title":"Основная идея: .md -> .html","slug":"основная-идея-md-html","link":"#основная-идея-md-html","children":[]},{"level":2,"title":"Наш инструментарий","slug":"наш-инструментарии","link":"#наш-инструментарии","children":[]},{"level":2,"title":"Логотип","slug":"логотип","link":"#логотип","children":[]}],"relativePath":"tools.md"}'),l={name:"tools.md"},i=r('<h1 id="как-работает-этот-саит" tabindex="-1">Как работает этот сайт <a class="header-anchor" href="#как-работает-этот-саит" aria-hidden="true">#</a></h1><h2 id="основная-идея-md-html" tabindex="-1">Основная идея: <code>.md -&gt; .html</code> <a class="header-anchor" href="#основная-идея-md-html" aria-hidden="true">#</a></h2><p>Статические генераторы используются для создания документации программных продуктов, а также для ведения блогов и создания других сайтов. Исходные материалы создаются в текстовом виде в разметке markdown и хранятся в системе контроля версий.</p><p>Генератор статического сайта применяет шаблоны и настройки, и создает страницы в формате HTML, которые можно разместить на любом хостинге. Не требуется подключение базы данных или систем управления контентом - полученные страницы HTML представляют собой готовый сайт.</p><p>Наиболее известные генераторы статических сайтов - Hugo и Jekyll, но вместе с ними развиваются и другие, в том числе более новые проекты.</p><h2 id="наш-инструментарии" tabindex="-1">Наш инструментарий <a class="header-anchor" href="#наш-инструментарии" aria-hidden="true">#</a></h2><p>В разработке сайта используются:</p><ul><li><a href="https://github.com/finec-mgimo/icode" target="_blank" rel="noreferrer">Github</a> - хранение исходных документов и кода, а также хостинг сайта (через Github Pages)</li><li><a href="https://vitepress.vuejs.org/" target="_blank" rel="noreferrer">VitePress</a> - статический генератор сайтов</li><li><a href="https://github.com/casey/just#installation" target="_blank" rel="noreferrer">just</a> - утилита для запуска типовых команд</li><li><a href="https://www.npmjs.com/package/prettier" target="_blank" rel="noreferrer">prettier</a> - форматирование текстов в markdown.</li></ul><h2 id="логотип" tabindex="-1">Логотип <a class="header-anchor" href="#логотип" aria-hidden="true">#</a></h2><p>Логотип Цифровой кафедры находится в стадии разработки. Эскиз представлен <a href="/icode/_logo.html">здесь</a>.</p>',10),h=[i];function o(n,s,d,c,_,p){return a(),t("div",null,h)}const f=e(l,[["render",o]]);export{u as __pageData,f as default};
